import {Injectable} from "@angular/core";
import {FaceSnap} from "../models/face-snap.model";


@Injectable({
  providedIn: 'root' // for "service" registered at the root => only one instance, the whole application share the same data and logic
})
export class FaceSnapsService {
  faceSnaps : FaceSnap[] = [
    {
    id: 1,
    title: 'Link',
    description: 'video game character : "the legend of zelda"',
    imageUrl: 'data:image/jpeg;base64,',
    createdDate: new Date(),
    snaps: 120,
    location: 'Paris'
    },
    {
      id: 2,
      title: 'Mario',
      description: 'video game character : "Mario Bross"',
      imageUrl: 'data:image/jpeg;base64,',
      createdDate: new Date(),
      snaps: 0,
      location: 'Brussels'
    },
    {
      id: 3,
      title: 'Captain America',
      description: 'video game character : "Marvel\'s Avengers"',
      imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQD4a2O9UbDyDcDVdLKXPqJ-tEUXeC-aDdWxw&usqp=CAU',
      createdDate: new Date(),
      snaps: 0
    },
    {
      id: 4,
      title: 'Link',
      description: 'video game character : "the legend of zelda"',
      imageUrl: 'data:image/jpeg;base64,',
      createdDate: new Date(),
      snaps: 0
    },
    {
      id: 5,
      title: 'Mario',
      description: 'video game character : "Mario Bross"',
      imageUrl: 'data:image/jpeg;base64,',
      createdDate: new Date(),
      snaps: 0
    },
    {
      id: 6,
      title: 'Captain America',
      description: 'video game character : "Marvel\'s Avengers"',
      imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQD4a2O9UbDyDcDVdLKXPqJ-tEUXeC-aDdWxw&usqp=CAU',
      createdDate: new Date(),
      snaps: 0
    }
  ];

  getAllFaceSnaps(): FaceSnap[]{
    return this.faceSnaps;
  }

  getFaceSnapById(faceSnapId: number): FaceSnap {
    const faceSnap = this.faceSnaps.find(faceSnap => faceSnap.id === faceSnapId);
    if(!faceSnap) {
      throw new Error('FaceSnap not found')
    } else {
      return faceSnap;
    }
  }

  snapFaceSnapById(faceSnapId : number, snapType: 'snap' | 'unsnap') : void{
    const faceSnap = this.getFaceSnapById(faceSnapId)
    snapType === 'snap' ? faceSnap.snaps++ : faceSnap.snaps--;
  }
}
